export const role = 'tablist';

export default (dc, config) => {

  dc.addType(config.typeTabContainer, {
    model: {
      defaults: {
        name: 'Tab Container',
        draggable: `[data-codx-type="${config.typeTabs}"]`,
        droppable: `[data-codx-type="${config.typeTab}"]`,
        copyable: false,
        removable: false,
        highlightable: false,
        attributes: { role },
        classes: config.classTabContainer,
        ...config.tabContainerProps,
      },
    },
  });
}
