import loadComponents from './components/index';
import loadBlocks from './blocks';
import defOptions from './options';

export default (editor, opts = {}) => {
  const options = {
    ...defOptions,
    ...opts
  };

  // Add components
  loadComponents(editor, options);

  // Add blocks
  loadBlocks(editor, options);

  editor.on('component:select:before', (component) => {
    // console.log(component, "Selected")


    if (component.attributes.type == "tab")
      if (component.getClasses().includes("tab-active")) {
        component.setClass("tab")
      } else {
        component.setClass("tab-active tab")
      }
  })
};
